import React, { useState } from 'react';

import './App.css';

const initialValues = {
  userName: '',
  userSurname: '',
  userSalary: '',
};

function App() {
  const [userData, setUserData] = useState(initialValues);
  const [users, setUsers] = useState([]);
  const [editableUserData, setEditableUserData] = useState({});

  const handleRemoveClick = (index) => {
    setUsers(users.filter((user, iserIndex) => iserIndex !== index));
  };

  const isFilledFields =
    userData.userName && userData.userSurname && userData.userSalary;

  const handleSubmitUser = (e) => {
    e.preventDefault();

    if (isFilledFields) {
      setUsers((prevState) => [...prevState, userData]);
      setUserData(initialValues);
    }
  };

  const handleCleanForm = () => setUserData(initialValues);

  console.log('users', users);

  return (
    <div className="App">
      <div className="wrapper">
        <div className="content">
          <table>
            <th>#</th>
            <th>User Name</th>
            <th>User Surname</th>
            <th>User Salary</th>
            <th>Actions</th>

            <tbody>
              {users.map((users, index) => (
                <tr>
                  <td>{index + 1}</td>
                  <td>{users.userName}</td>
                  <td>{users.userSurname}</td>
                  <td>{users.userSalary}</td>
                  <td>
                    <div>
                      <button className="edit">edit</button>
                      <button
                        className="remove"
                        onClick={() => handleRemoveClick(index)}
                      >
                        remove
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="inputs">
          <form onSubmit={handleSubmitUser} onReset={handleCleanForm}>
            <input
              placeholder="Write your name"
              onChange={(e) =>
                setUserData((prevState) => ({
                  ...prevState,
                  userName: e.target.value,
                }))
              }
              value={userData.userName}
            />
            <input
              placeholder="Write your surname"
              onChange={(e) =>
                setUserData((prevState) => ({
                  ...prevState,
                  userSurname: e.target.value,
                }))
              }
              value={userData.userSurname}
            />
            <input
              placeholder="Write your salary"
              onChange={(e) =>
                setUserData((prevState) => ({
                  ...prevState,
                  userSalary: e.target.value,
                }))
              }
              value={userData.userSalary}
            />

            <div>
              <button disabled={!isFilledFields} type="submit">
                Add
              </button>
              <button type="reset">Clean</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
